/**
 * App.tsx
 * ‰ΩøÁî®Êó•ÂøóÁÆ°ÁêÜÁªÑ‰ª∂
 * ÂäüËÉΩÔºöÊèê‰æõËµÑÊ∫ê‰ΩøÁî®Êó•ÂøóÁöÑÂΩïÂÖ•ÂíåÂ±ïÁ§∫ÂäüËÉΩÔºåÂåÖÊã¨ËÆæÂ§áÂíåÊùêÊñôÁöÑ‰ΩøÁî®ËÆ∞ÂΩï
 */

import React, { useEffect, useState } from "react";
import axios from "axios";
import { Spin, Form, Select, Input, InputNumber, Checkbox, Button, Table, message, Space } from "antd";
import "antd/dist/reset.css"; // Â¶ÇÊûú‰ΩøÁî® antd v5+Ôºå‰ΩøÁî®Ê≠§Ë°å

import { UsageLog, ResourceType, ActionType } from "./types";

// Â∏∏ÈáèÂÆö‰πâ
const API_BASE = "http://127.0.0.1:8000/api/logs/";

const FORM_RULES = {
  resource_id: [{ required: true, message: "ËØ∑ËæìÂÖ•ËµÑÊ∫êID" }],
  resource_type: [{ required: true, message: "ËØ∑ÈÄâÊã©ËµÑÊ∫êÁ±ªÂûã" }],
  action: [{ required: true, message: "ËØ∑ÈÄâÊã©Êìç‰ΩúÁ±ªÂûã" }],
  quantity_used: [{ required: true, type: "number" as const, min: 0, message: "Êï∞ÈáèÂøÖÈ°ªÂ§ß‰∫é0" }],
  duration_minutes: [{ required: true, type: "number" as const, min: 0, message: "Êó∂ÈïøÂøÖÈ°ªÂ§ß‰∫é0" }]
};

/**
 * ‰ΩøÁî®Êó•ÂøóÁÆ°ÁêÜ‰∏ªÁªÑ‰ª∂
 * @component App
 */
const App: React.FC = () => {
  const [form] = Form.useForm();
  const [logs, setLogs] = useState<UsageLog[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  /** Áî®Êà∑ËÆ§ËØÅtoken */
  const token = localStorage.getItem("access_token") || "";

  /**
   * tokenÊúâÊïàÊÄßÊ£ÄÊü•
   * ÂâØ‰ΩúÁî®ÔºöÂΩìtoken‰∏çÂ≠òÂú®Êó∂ËÆæÁΩÆÈîôËØØ‰ø°ÊÅØ
   */
  useEffect(() => {
    if (!token) {
      setError("Êú™Ê£ÄÊµãÂà∞ÁôªÂΩï‰ø°ÊÅØÔºåËØ∑ÂÖàÁôªÂΩï„ÄÇ");
    }
  }, [token]);

  /**
   * Ëé∑ÂèñÊó•ÂøóÂàóË°®
   * ÂâØ‰ΩúÁî®ÔºöÁªÑ‰ª∂Âä†ËΩΩÊó∂‰ªéAPIËé∑ÂèñÊó•ÂøóÊï∞ÊçÆ
   */
  useEffect(() => {
    if (!token) return;
    setLoading(true);
    axios
      .get(API_BASE, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setLogs(res.data as UsageLog[]);
        setError(null);
        setLoading(false);
      })
      .catch((err) => {
        const errorMsg = err.response?.data?.detail || err.message;
        setError("Ëé∑ÂèñÊó•ÂøóÂ§±Ë¥•: " + errorMsg);
        message.error("Ëé∑ÂèñÊó•ÂøóÂ§±Ë¥•: " + errorMsg);
        setLoading(false);
      });
  }, [token]);

  /**
   * Â§ÑÁêÜË°®ÂçïÊèê‰∫§
   * @param {Partial<UsageLog>} values - Ë°®ÂçïÊï∞ÊçÆ
   */
  const handleSubmit = async (values: Partial<UsageLog>) => {
    if (!token) {
      message.error("Êú™Ê£ÄÊµãÂà∞ÁôªÂΩï‰ø°ÊÅØÔºåËØ∑ÂÖàÁôªÂΩï„ÄÇ");
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(
        API_BASE,
        values,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setLogs([response.data as UsageLog, ...logs]);
      form.resetFields();
      message.success("Êó•ÂøóÊ∑ªÂä†ÊàêÂäü");
    } catch (err: any) {
      const errorMsg = err.response?.data?.detail || err.message;
      message.error("Êèê‰∫§Â§±Ë¥•: " + errorMsg);
      setError("Êèê‰∫§Â§±Ë¥•: " + errorMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Spin spinning={loading}>
      <div style={{ maxWidth: 900, margin: "40px auto", padding: 24, background: "#fff", borderRadius: 12, boxShadow: "0 2px 8px rgba(0,0,0,0.1)" }}>
        <h2>üìù ‰ΩøÁî®Êó•ÂøóÁÆ°ÁêÜ</h2>
        {/* ÈîôËØØ‰ø°ÊÅØÂ±ïÁ§∫Âå∫Âüü */}
        {error && <div style={{ color: "red", marginBottom: 16 }}>{error}</div>}

        <Form
          form={form}
          onFinish={handleSubmit}
          layout="vertical"
          style={{ marginBottom: 32 }}
          initialValues={{
            resource_type: ResourceType.EQUIPMENT,
            action: ActionType.START_USE,
            auto_recorded: false
          }}
        >
          <Form.Item name="resource_type" label="ËµÑÊ∫êÁ±ªÂûã" rules={FORM_RULES.resource_type}>
            <Select>
              <Select.Option value={ResourceType.EQUIPMENT}>ËÆæÂ§á</Select.Option>
              <Select.Option value={ResourceType.MATERIAL}>ÊùêÊñô</Select.Option>
            </Select>
          </Form.Item>

          {/* ËµÑÊ∫êIDËæìÂÖ•Ê°ÜÔºöÂøÖÂ°´Â≠óÊÆµ */}
          <Form.Item name="resource_id" label="ËµÑÊ∫êID" rules={FORM_RULES.resource_id}>
            <InputNumber min={1} style={{ width: '100%' }} />
          </Form.Item>

          {/* Êìç‰ΩúÁ±ªÂûãÈÄâÊã©ÔºöÂåÖÂê´ÂºÄÂßã‰ΩøÁî®/ÁªìÊùü‰ΩøÁî®/Ê∂àËÄó/Áª¥Êä§ */}
          <Form.Item name="action" label="Êìç‰ΩúÁ±ªÂûã" rules={FORM_RULES.action}>
            <Select>
